mapper.select:
SELECT EMAIL 
FROM COMMUNITYMEMBER 
WHERE EMAIL = #{email}

SELECT EMAIL, NAME, NICKNAME, PHONENUMBER, BIRTHDAY 
FROM COMMUNITYMEMBER 
WHERE EMAIL = #{email}

SELECT EMAIL, NAME, NICKNAME, PHONENUMBER, BIRTHDAY 
FROM COMMUNITYMEMBER 
WHERE NAME = #{name}

mapper.update:
UPDATE COMMUNITYMEMBER 
SET NICKNAME = #{nickName}, PHONENUMBER = #{phoneNumber} , BIRTHDAY = #{birthDay} 
WHERE EMAIL = #{email}
