FROM CLUBMEMBERSHIP
WHERE MEMBERNAME = #{memberName}

SELECT CLUBID, MEMBEREMAIL, MEMBERNAME, ROLE, JOINDATE
FROM CLUBMEMBERSHIP
WHERE MEMBEREMAIL = #{memberEmail}
